command:
const options = foundry.utils.deepClone({
  advantage: window.event?.altKey,
  disadvantage: window.event?.shiftKey,
  noHeighten: true,
})
const spells = {
  flameRay: "Flame Ray",
  lightRay: "Light Ray",
  empathicRay: "Empathic Ray",
  deathRay: "Death Ray",
  lightningRay: "Lightning Ray",
  fireBall: "Fire Ball",
  medicineBall: "Medicine Ball",
  treeformBall: "Treeform Ball",
  terrorBall: "Terror Ball",
  iceBall: "Ice Ball",
}
const chosenAbilityName = await game.teriock.api.dialog.select(spells, "Missile Spell", "Please select a missile spell.", "Select Missile Spell");
const actor = scope.abilityData.actor;
const ability = await game.teriock.api.import.ability(actor, spells[chosenAbilityName]);
if (scope.abilityData.parent.isFluent) {
  await ability.update({
    "system.fluent": true,
  })
} else if (scope.abilityData.parent.isProficient) {
  await ability.update({
    "system.proficient": true,
  })
}
Hooks.once("renderChatMessageHTML", async () => {
  await ability.system.roll(options);
  await ability.delete();
});
